import { useState } from 'react'

import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

const ArtistRegister = ({ onRegister }) => {
  const [name, setName] = useState('')
  const [job, setJob] = useState('')

  const onSubmit = (e) => {
    e.preventDefault()

    if (!name) {
      alert("Un nom d'artiste est obligatoire")
      return
    }

    if (!job) {
      alert("Un métier est obligatoire")
      return
    }

    // Call onRegister with form values
    onRegister({ name, job })

    // Reset form
    setName('')
    setJob('')
  }

  return (
    <div className="ArtistRegister">
      <h2>Vous inscrire en tant qu'artiste</h2>

      <Form onSubmit={onSubmit}>
        <Form.Group controlId="artistRegisterName">
          <Form.Label>Nom d'artiste</Form.Label>
          <Form.Control type="text" placeholder="Votre nom d'artiste" value={name}
                                                                      onChange={(e) => setName(e.target.value)} />
        </Form.Group>

        <Form.Group controlId="artistRegisterJob">
          <Form.Label>Métier</Form.Label>
          <Form.Control type="text" placeholder="Votre métier dans le spectacle" value={job}
                                                                                 onChange={(e) => setJob(e.target.value)} />
        </Form.Group>

        <Button variant="primary" type="submit">S'inscrire</Button>
      </Form>
    </div>
  )
}

export default ArtistRegister
