import { useState } from 'react'

import { Link } from 'react-router-dom'
import axios from 'axios'

import PageTitle from '../utils/titles/PageTitle'

import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

const ArtistRegister = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    passwordCheck: '',
  })

  const { email, password, passwordCheck } = formData

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value })

  const onSubmit = async (e) => {
    e.preventDefault()

    if (password !== passwordCheck) {
      console.log('Les mots de passe ne sont pas identiques')
    } else {
      const newArtist = { email, password }

      try {
        const config = {
          headers: {
            'Content-Type': 'application/json',
          },
        }

        const body = JSON.stringify(newArtist)

        const res = await axios.post('/api/artists', body, config)
      } catch (err) {}
    }
  }

  return (
    <div>
      <PageTitle>Vous inscrire en tant qu'artiste</PageTitle>

      <Row>
        <Col md={{ span: 4, offset: 4 }} lg={{ span: 4, offset: 4 }}>
          <Form onSubmit={(e) => onSubmit(e)}>
            <Form.Group controlId='form-email'>
              <Form.Label>Adresse email</Form.Label>
              <Form.Control
                type='email'
                placeholder='Votre adresse email (doit être valide)'
                name='email'
                value={email}
                onChange={(e) => onChange(e)}
                required
              />
            </Form.Group>

            <Form.Group controlId='form-password'>
              <Form.Label>Mot de passe</Form.Label>
              <Form.Control
                type='password'
                name='password'
                value={password}
                onChange={(e) => onChange(e)}
                required
              />
            </Form.Group>

            <Form.Group controlId='form-password-check'>
              <Form.Label>Mot de passe (confirmation)</Form.Label>
              <Form.Control
                type='password'
                name='passwordCheck'
                value={passwordCheck}
                onChange={(e) => onChange(e)}
                required
              />
            </Form.Group>

            <Button variant='primary' type='submit'>
              Inscription
            </Button>
          </Form>

          <Form.Text muted>
            Vous êtes déjà inscrit sur notre plateforme ?{' '}
            <Link to='/login'>Connexion</Link>
          </Form.Text>
        </Col>
      </Row>
    </div>
  )
}

export default ArtistRegister
