// Login will be same page for both artist and enterprises
// Backend will determine which is who

import { useState } from 'react'

import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

import Form from 'react-bootstrap/Form'

import PageTitle from '../utils/titles/PageTitle'

const [formData, setFormData] = useState({
  email: '',
  password: '',
})

const { email, password } = formData

const onChange = (e) => {
  setFormData({ ...formData, [e.target.name]: e.target.value })
}

const onSubmit = async (e) => {
  e.preventDefault()

  console.log('login')
}

const Login = () => {
  return (
    <div>
      <PageTitle>Vous identifier</PageTitle>

      <Row>
        <Col md={{ span: 4, offset: 4 }} lg={{ span: 4, offset: 4 }}>
          <Form>
            <Form.Group controlId='form-email'>
              <Form.Label>Adresse email</Form.Label>
              <Form.Control
                type='email'
                placeholder='Votre adresse email'
                name='email'
                value={email}
                onChange={(e) => onChange(e)}
              />
            </Form.Group>
          </Form>
        </Col>
      </Row>
    </div>
  )
}

export default Login
